import React from "react";

export default function FaceOffPanel({
  videoA, videoB,
  revealedA, revealedB,
  onRevealA, onRevealB,
  onVote
}) {
  return (
    <div className="min-h-screen w-full flex flex-col items-center justify-center vs-bg vs-vignette relative overflow-hidden animate-fade-in">
      {/* Diagonal VS lighting */}
      <div className="absolute inset-0 pointer-events-none z-0" style={{
        background: "linear-gradient(105deg, #3c096c44 0%, #c77dff11 80%)"
      }} />
      {/* Versus graphics */}
      <div className="absolute left-1/2 top-12 transform -translate-x-1/2 z-10">
        <span className="text-[7rem] font-extrabold text-transparent bg-gradient-to-tr from-[#ff77e9] to-[#7b2cbf] bg-clip-text drop-shadow-[0_2px_20px_rgba(199,125,255,0.8)] animate-pop">
          VS
        </span>
      </div>
      {/* Face-off */}
      <div className="relative z-20 flex flex-row w-full max-w-7xl items-center justify-center gap-24 py-16">
        {/* LEFT */}
        <div className="flex-1 flex flex-col items-center">
          {!revealedA ? (
            <button
              onClick={onRevealA}
              className="bg-[#3c096c] bg-opacity-80 text-white px-16 py-10 rounded-2xl text-3xl font-black border-4 border-[#7b2cbf] shadow-2xl hover:bg-[#5a189a] animate-pop transition"
            >Reveal</button>
          ) : (
            <>
              <div className="relative animate-pop">
                <iframe
                  width="540"
                  height="304"
                  src={`https://www.youtube.com/embed/${videoA.ytId}`}
                  title={videoA.title}
                  frameBorder="0"
                  allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                  allowFullScreen
                  className="rounded-2xl shadow-xl border-4 border-[#c77dff] scale-105"
                ></iframe>
                <div className="absolute top-0 left-0 right-0 bg-[#7b2cbf44] h-12 rounded-t-2xl flex items-center justify-center text-lg font-bold text-white tracking-wide">
                  {videoA.title}
                </div>
              </div>
            </>
          )}
        </div>
        {/* RIGHT */}
        <div className="flex-1 flex flex-col items-center">
          {!revealedB ? (
            <button
              onClick={onRevealB}
              className="bg-[#7b2cbf] bg-opacity-80 text-white px-16 py-10 rounded-2xl text-3xl font-black border-4 border-[#c77dff] shadow-2xl hover:bg-[#9d4edd] animate-pop transition"
            >Reveal</button>
          ) : (
            <>
              <div className="relative animate-pop">
                <iframe
                  width="540"
                  height="304"
                  src={`https://www.youtube.com/embed/${videoB.ytId}`}
                  title={videoB.title}
                  frameBorder="0"
                  allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                  allowFullScreen
                  className="rounded-2xl shadow-xl border-4 border-[#7b2cbf] scale-105"
                ></iframe>
                <div className="absolute top-0 left-0 right-0 bg-[#c77dff44] h-12 rounded-t-2xl flex items-center justify-center text-lg font-bold text-white tracking-wide">
                  {videoB.title}
                </div>
              </div>
            </>
          )}
        </div>
      </div>
      {/* Vote buttons appear only if both revealed */}
      {revealedA && revealedB && (
        <div className="flex gap-32 z-30 mt-8 animate-pop">
          <button onClick={() => onVote(videoA.id)}
                  className="btn text-2xl px-14 py-7 bg-[#7b2cbf] hover:bg-[#c77dff] border-4 border-[#c77dff] shadow-xl font-black uppercase tracking-widest rounded-2xl transition animate-pop">
            Vote Left
          </button>
          <button onClick={() => onVote(videoB.id)}
                  className="btn text-2xl px-14 py-7 bg-[#c77dff] hover:bg-[#7b2cbf] border-4 border-[#7b2cbf] shadow-xl font-black uppercase tracking-widest rounded-2xl transition animate-pop">
            Vote Right
          </button>
        </div>
      )}
    </div>
  );
}
