import React, { useState } from 'react';

const BracketDisplay = ({ tournament }) => {
  const [selectedMatch, setSelectedMatch] = useState(null); // Store the selected match for the modal

  // Helper function to get the winning side
  const getVoteLeader = (match) => {
    const votesA = (tournament.votes || []).filter(v => v.matchId === match.id && v.votedFor === match.videoAId).length;
    const votesB = (tournament.votes || []).filter(v => v.matchId === match.id && v.votedFor === match.videoBId).length;
    return votesA > votesB ? 'A' : (votesB > votesA ? 'B' : null);
  };

  const handleMatchClick = (match) => {
    setSelectedMatch(match); // Set the selected match to display its details
  };

  const closeModal = () => {
    setSelectedMatch(null); // Close the modal
  };

  if (!tournament || !tournament.bracket || !Array.isArray(tournament.bracket)) return <div>No tournament bracket available.</div>;
  if (!Array.isArray(tournament.videos)) return <div>No videos available.</div>;
  if (tournament.bracket.length === 0) return <div>No rounds available.</div>;

  return (
    <div className="overflow-x-auto w-full flex justify-center pb-10">
      <div className="flex gap-12 min-h-[400px] flex-col sm:flex-row">
        {tournament.bracket.map((round, ridx) => (
          <div
            key={ridx}
            className="flex flex-col gap-5 min-w-[300px] bg-gradient-to-b from-[--main-bg] to-[--main-bg] rounded-2xl shadow-lg border-2 border-[#--main-bg] px-4 py-3"
          >
            <div className="font-extrabold text-xl text-center tracking-wider text-[#--main-bg] drop-shadow pb-2">
              Round {round.round}
            </div>
            {(round.matches || []).map((match) => {
              const vidA = tournament.videos.find(v => v.id === match.videoAId);
              const vidB = tournament.videos.find(v => v.id === match.videoBId);
              const leader = getVoteLeader(match);
              return (
                <div
                  key={match.id}
                  className="bg-[--main-bg] border-2 border-[#--main-bg] rounded-xl shadow-xl flex flex-col items-center py-4 px-2 hover:border-[#--main-bg] transition-all"
                  onClick={() => handleMatchClick(match)} // Handle match click
                >
                  <div className="w-full flex flex-col items-center">
                    <span className={`font-bold text-lg text-center max-w-[200px] truncate ${leader === 'A' ? 'text-blue-500' : 'text-gray-300'}`}>
                      {vidA?.title || 'A'}
                    </span>
                    <span className="text-[#--main-bg] text-sm my-1">vs</span>
                    <span className={`font-bold text-lg text-center max-w-[200px] truncate ${leader === 'B' ? 'text-red-500' : 'text-gray-300'}`}>
                      {vidB?.title || 'B'}
                    </span>
                  </div>
                  <div className="text-xs text-[#--main-bg] mt-3">
                    <span className="font-semibold text-[#--main-bg]">A:</span> {(tournament.votes || []).filter(v => v.matchId === match.id && v.votedFor === match.videoAId).length}
                    <span className="mx-1 text-[#--main-bg]">|</span>
                    <span className="font-semibold text-[#--main-bg]">B:</span> {(tournament.votes || []).filter(v => v.matchId === match.id && v.votedFor === match.videoBId).length}
                  </div>
                </div>
              );
            })}
          </div>
        ))}
      </div>

      {/* Modal for Vote Details */}
      {selectedMatch && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50">
          <div className="bg-white p-6 rounded-lg shadow-lg w-96">
            <h3 className="text-xl font-bold mb-4">Vote Details for Match</h3>
            <p><strong>Match ID:</strong> {selectedMatch.id}</p>
            <p><strong>Video A:</strong> {tournament.videos.find(v => v.id === selectedMatch.videoAId)?.title}</p>
            <p><strong>Video B:</strong> {tournament.videos.find(v => v.id === selectedMatch.videoBId)?.title}</p>

            <div className="mt-4">
              <h4 className="font-semibold">Votes:</h4>
              <div>
                <strong>A:</strong>
                <ul>
                  {(tournament.votes || []).filter(v => v.matchId === selectedMatch.id && v.votedFor === selectedMatch.videoAId).map((vote, index) => (
                    <li key={index}>{vote.username}</li> // Assuming each vote has a "username" field
                  ))}
                </ul>
              </div>
              <div className="mt-2">
                <strong>B:</strong>
                <ul>
                  {(tournament.votes || []).filter(v => v.matchId === selectedMatch.id && v.votedFor === selectedMatch.videoBId).map((vote, index) => (
                    <li key={index}>{vote.username}</li> // Assuming each vote has a "username" field
                  ))}
                </ul>
              </div>
            </div>

            <button
              className="mt-4 bg-blue-500 text-white px-4 py-2 rounded"
              onClick={closeModal}
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default BracketDisplay;
