import React from 'react';

// Palette for theme (updated to more tournament-like colors)
const roundColors = [
  "from-[#2d0067] to-[#9d4edd]",   // Round 1 (Dark purple to a lighter purple)
  "from-[#1a0b4e] to-[#8b40ff]",   // Round 2
  "from-[#4e0f5b] to-[#c77dff]",   // Round 3
  "from-[#9d4edd] to-[#c77dff]",   // Round 4+
];

export default function BracketDisplay({ tournament }) {
  if (!tournament?.bracket || !Array.isArray(tournament.bracket)) return null;
  if (!Array.isArray(tournament.videos)) return <div className="text-red-500">No videos data</div>;
  if (tournament.bracket.length === 0) return <div className="text-gray-500">No rounds available.</div>;

  return (
    <div className="overflow-x-auto w-full flex justify-center pb-10">
      <div className="flex gap-10 min-h-[350px] w-full justify-between">
        {tournament.bracket.map((round, ridx) => (
          <div
            key={ridx}
            className={`
              flex flex-col gap-5 w-full max-w-xs
              bg-gradient-to-b ${roundColors[ridx % roundColors.length]} rounded-2xl shadow-lg border-2 border-[#7b2cbf]/50 px-4 py-3
            `}
          >
            <div className="font-extrabold text-xl text-center tracking-wider text-white drop-shadow pb-2">
              Round {round.round}
            </div>
            {(round.matches || []).map((m, i) => {
              const vidA = tournament.videos.find(v => v.id === m.videoAId);
              const vidB = tournament.videos.find(v => v.id === m.videoBId);
              const votesA = (tournament.votes || []).filter(v => v.matchId === m.id && v.votedFor === m.videoAId).length;
              const votesB = (tournament.votes || []).filter(v => v.matchId === m.id && v.votedFor === m.videoBId).length;

              // Determine the leader and apply a highlight style
              const leader = votesA > votesB ? 'A' : 'B';
              const leaderColor = leader === 'A' ? 'border-yellow-500' : 'border-red-500';

              return (
                <div
                  key={m.id}
                  className={`
                    bg-[#240046] border-2 ${leaderColor} rounded-xl shadow-xl flex flex-col items-center py-4 px-2
                    hover:border-[#c77dff] transition-all cursor-pointer
                  `}
                  onClick={() => alert(`Match: ${vidA?.title} vs ${vidB?.title} | Votes: A: ${votesA} | B: ${votesB}`)}
                >
                  <div className="w-full flex flex-col items-center">
                    <span className="font-bold text-[#c77dff] text-lg text-center max-w-[200px] truncate">{vidA?.title || 'A'}</span>
                    <span className="text-white/70 text-sm my-1">vs</span>
                    <span className="font-bold text-[#7b2cbf] text-lg text-center max-w-[200px] truncate">{vidB?.title || 'B'}</span>
                  </div>
                  <div className="text-xs text-white/80 mt-3">
                    <span className="font-semibold text-[#c77dff]">A:</span> {votesA}
                    <span className="mx-1 text-white/50">|</span>
                    <span className="font-semibold text-[#7b2cbf]">B:</span> {votesB}
                  </div>
                </div>
              );
            })}
          </div>
        ))}
      </div>
    </div>
  );
}
