import React from 'react';

const BracketDisplay = ({ tournament }) => {
  // Helper function to get the winning side
  const getVoteLeader = (match) => {
    const votesA = (tournament.votes || []).filter(v => v.matchId === match.id && v.votedFor === match.videoAId).length;
    const votesB = (tournament.votes || []).filter(v => v.matchId === match.id && v.votedFor === match.videoBId).length;
    return votesA > votesB ? 'A' : (votesB > votesA ? 'B' : null);
  };

  if (!tournament || !tournament.bracket || !Array.isArray(tournament.bracket)) return <div>No tournament bracket available.</div>;
  if (!Array.isArray(tournament.videos)) return <div>No videos available.</div>;
  if (tournament.bracket.length === 0) return <div>No rounds available.</div>;

  return (
    <div className="overflow-x-auto w-full flex justify-center pb-10">
      <div className="flex gap-12 min-h-[400px] flex-col sm:flex-row">
        {tournament.bracket.map((round, ridx) => (
          <div
            key={ridx}
            className="flex flex-col gap-5 min-w-[300px] bg-gradient-to-b from-[--main-bg] to-[--main-bg] rounded-2xl shadow-lg border-2 border-[#c77dff] px-4 py-3"
          >
            <div className="font-extrabold text-xl text-center tracking-wider text-[#c77dff] drop-shadow pb-2">
              Round {round.round}
            </div>
            {(round.matches || []).map((match, i) => {
              const vidA = tournament.videos.find(v => v.id === match.videoAId);
              const vidB = tournament.videos.find(v => v.id === match.videoBId);
              const leader = getVoteLeader(match);
              return (
                <div
                  key={match.id}
                  className="bg-[--main-bg] border-2 border-[#9d4edd] rounded-xl shadow-xl flex flex-col items-center py-4 px-2 hover:border-[#c77dff] transition-all"
                >
                  <div className="w-full flex flex-col items-center">
                    <span className={`font-bold text-lg text-center max-w-[200px] truncate ${leader === 'A' ? 'text-blue-500' : 'text-gray-300'}`}>
                      {vidA?.title || 'A'}
                    </span>
                    <span className="text-[#e0aaff] text-sm my-1">vs</span>
                    <span className={`font-bold text-lg text-center max-w-[200px] truncate ${leader === 'B' ? 'text-red-500' : 'text-gray-300'}`}>
                      {vidB?.title || 'B'}
                    </span>
                  </div>
                  <div className="text-xs text-[#e0aaff] mt-3">
                    <span className="font-semibold text-[#c77dff]">A:</span> {(tournament.votes || []).filter(v => v.matchId === match.id && v.votedFor === match.videoAId).length}
                    <span className="mx-1 text-[#e0aaff]">|</span>
                    <span className="font-semibold text-[#7b2cbf]">B:</span> {(tournament.votes || []).filter(v => v.matchId === match.id && v.votedFor === match.videoBId).length}
                  </div>
                </div>
              );
            })}
          </div>
        ))}
      </div>
    </div>
  );
};

export default BracketDisplay;
