import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { listenTournament } from '../firebase/firestore';
import BracketDisplay from './BracketDisplay';

// Custom theme background
const themeBg = {
  violet: "bg-gradient-to-br from-[var(--main-dark)] via-[var(--main-gold-dark)] to-[var(--main-gold)]",
  dark: "bg-[var(--main-bg)]",
};

function BracketView() {
  const { tid } = useParams();
  const [tournament, setTournament] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const unsub = listenTournament(tid, (data) => {
      setTournament(data);
      setLoading(false);
    });
    return () => unsub && unsub();
  }, [tid]);

  if (loading || !tournament)
    return (
      <div className="flex justify-center items-center min-h-screen bg-[var(--main-bg)]">
        <span className="text-[var(--main-gold-dark)] font-bold text-xl animate-pulse">Loading...</span>
      </div>
    );

  if (!Array.isArray(tournament.bracket) || tournament.bracket.length === 0)
    return (
      <div className="flex justify-center items-center min-h-screen bg-[var(--main-bg)]">
        <span className="text-lg text-[var(--main-gold)]/70">No bracket available.</span>
      </div>
    );

  return (
    <div className={`${themeBg.violet} min-h-screen w-screen flex flex-col items-stretch`}>
      <div className="w-full mx-auto pt-16 px-4 flex flex-col items-center">
        <h2 className="text-5xl font-extrabold tracking-tight text-center mb-12 text-[var(--main-gold)] drop-shadow-lg">
          Live Bracket
        </h2>
        <div className="w-full max-w-7xl animate-fade-in">
          <BracketDisplay tournament={tournament} />
        </div>
      </div>
    </div>
  );
}

export default BracketView;
