import React, { useEffect, useState } from "react";
import { motion, AnimatePresence } from "framer-motion";

// Palette
const leftBorder = "border-tekhelet-600";
const rightBorder = "border-french_violet-600";

export default function FaceOffPanel({
  videoA, videoB,
  revealedA, revealedB,
  onRevealA, onRevealB,
  onVote
}) {
  const [collide, setCollide] = useState(false);
  const [flash, setFlash] = useState(false);

  // Trigger "collision" only once, when both revealed
  useEffect(() => {
    if (revealedA && revealedB && !collide) {
      setTimeout(() => setFlash(true), 350);  // white flash, optional
      setTimeout(() => setCollide(true), 400);
      setTimeout(() => setFlash(false), 600);
    }
    if (!(revealedA && revealedB)) setCollide(false); // reset if not both
  }, [revealedA, revealedB]);

  return (
    <div className="relative min-h-screen w-full flex flex-col items-center justify-center bg-gradient-to-tr from-russian_violet-900 to-persian_indigo-800 overflow-hidden">
      {/* VS Glow & shockwave */}
      <AnimatePresence>
        {collide &&
          <motion.div
            key="vs-effect"
            initial={{ scale: 0.9, opacity: 0.4 }}
            animate={{ scale: [1.5, 1, 1.15, 1], opacity: [1, 1, 0.8, 1] }}
            exit={{ scale: 0.9, opacity: 0 }}
            transition={{ duration: 0.7, times: [0, 0.35, 0.7, 1] }}
            className="absolute left-1/2 top-1/2 -translate-x-1/2 -translate-y-1/2 pointer-events-none z-30"
          >
            <div className="text-[10vw] font-extrabold text-center text-heliotrope-400 drop-shadow-[0_0_32px_#fff,0_0_48px_#c77dff] animate-pulse select-none">
              VS
            </div>
          </motion.div>
        }
      </AnimatePresence>
      {/* White Flash */}
      {flash && (
        <motion.div
          className="absolute inset-0 bg-white/80 z-40"
          initial={{ opacity: 0.8 }}
          animate={{ opacity: 0 }}
          exit={{ opacity: 0 }}
          transition={{ duration: 0.25 }}
        />
      )}
      {/* Panel Row */}
      <div className="relative z-10 flex flex-row w-full max-w-7xl items-center justify-center gap-14 pt-24 pb-16">
        {/* LEFT */}
        <div className="flex-1 flex flex-col items-center justify-center">
          {!revealedA ? (
            <motion.button
              initial={{ scale: 0.9, opacity: 0.5 }}
              animate={{ scale: 1, opacity: 1 }}
              whileHover={{ scale: 1.06 }}
              whileTap={{ scale: 0.93 }}
              onClick={onRevealA}
              className="bg-russian_violet-700 text-white px-16 py-10 rounded-2xl text-3xl font-bold shadow-xl border-4 border-french_violet-700 focus:ring-2 ring-french_violet-500 transition-all"
            >Reveal</motion.button>
          ) : (
            <motion.div
              initial={{ x: -140, scale: 0.9, opacity: 0 }}
              animate={{
                x: collide ? 0 : -70,
                scale: collide ? 1.08 : 0.98,
                opacity: 1
              }}
              transition={{ type: "spring", stiffness: 260, damping: 15 }}
              className={`w-[530px] h-[298px] bg-black rounded-2xl shadow-xl border-4 ${leftBorder} relative overflow-hidden flex items-center`}
            >
              <iframe
                width="100%"
                height="100%"
                src={`https://www.youtube.com/embed/${videoA.ytId}`}
                title={videoA.title}
                frameBorder="0"
                allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                allowFullScreen
                className="rounded-2xl"
              ></iframe>
              <div className="absolute left-0 bottom-0 w-full px-4 py-3 bg-gradient-to-t from-black/80 to-transparent text-lg font-bold text-french_violet-300 text-center">
                {videoA.title}
              </div>
            </motion.div>
          )}
        </div>
        {/* RIGHT */}
        <div className="flex-1 flex flex-col items-center justify-center">
          {!revealedB ? (
            <motion.button
              initial={{ scale: 0.9, opacity: 0.5 }}
              animate={{ scale: 1, opacity: 1 }}
              whileHover={{ scale: 1.06 }}
              whileTap={{ scale: 0.93 }}
              onClick={onRevealB}
              className="bg-russian_violet-700 text-white px-16 py-10 rounded-2xl text-3xl font-bold shadow-xl border-4 border-heliotrope-600 focus:ring-2 ring-heliotrope-400 transition-all"
            >Reveal</motion.button>
          ) : (
            <motion.div
              initial={{ x: 140, scale: 0.9, opacity: 0 }}
              animate={{
                x: collide ? 0 : 70,
                scale: collide ? 1.08 : 0.98,
                opacity: 1
              }}
              transition={{ type: "spring", stiffness: 260, damping: 15 }}
              className={`w-[530px] h-[298px] bg-black rounded-2xl shadow-xl border-4 ${rightBorder} relative overflow-hidden flex items-center`}
            >
              <iframe
                width="100%"
                height="100%"
                src={`https://www.youtube.com/embed/${videoB.ytId}`}
                title={videoB.title}
                frameBorder="0"
                allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                allowFullScreen
                className="rounded-2xl"
              ></iframe>
              <div className="absolute left-0 bottom-0 w-full px-4 py-3 bg-gradient-to-t from-black/80 to-transparent text-lg font-bold text-heliotrope-300 text-center">
                {videoB.title}
              </div>
            </motion.div>
          )}
        </div>
      </div>
      {/* Vote Buttons */}
      <AnimatePresence>
        {collide && (
          <motion.div
            key="votes"
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: 20 }}
            transition={{ duration: 0.3, delay: 0.2 }}
            className="flex gap-16 z-20 mt-2"
          >
            <motion.button
              whileHover={{ scale: 1.10, backgroundColor: "#5a189a" }}
              whileTap={{ scale: 0.98 }}
              onClick={() => onVote(videoA.id)}
              className="btn text-2xl px-12 py-6 font-extrabold rounded-2xl bg-french_violet-600 hover:bg-french_violet-500 text-white shadow-xl border-2 border-french_violet-400 transition-all"
            >
              👈 Vote Left
            </motion.button>
            <motion.button
              whileHover={{ scale: 1.10, backgroundColor: "#c77dff" }}
              whileTap={{ scale: 0.98 }}
              onClick={() => onVote(videoB.id)}
              className="btn text-2xl px-12 py-6 font-extrabold rounded-2xl bg-heliotrope-600 hover:bg-heliotrope-500 text-white shadow-xl border-2 border-heliotrope-400 transition-all"
            >
              Vote Right 👉
            </motion.button>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}
