import React, { useEffect, useState } from "react";
import { motion, AnimatePresence } from "framer-motion";

// ----- Background Noise SVG (inline for performance) -----
function NoiseTexture() {
  return (
    <svg className="absolute inset-0 w-full h-full z-0 pointer-events-none" style={{mixBlendMode: "soft-light"}} width="100%" height="100%">
      <filter id="noiseFilter">
        <feTurbulence type="fractalNoise" baseFrequency="0.95" numOctaves="3" stitchTiles="stitch"/>
      </filter>
      <rect width="100%" height="100%" filter="url(#noiseFilter)" opacity="0.09" />
    </svg>
  );
}

// ----- Animated Particles -----
function ParticleBG() {
  // 18 animated particles, randomized positions, simple floating
  return (
    <div className="pointer-events-none absolute inset-0 z-10 overflow-hidden">
      {Array.from({ length: 18 }).map((_, i) => (
        <motion.div
          key={i}
          initial={{
            top: `${Math.random() * 100}%`,
            left: `${Math.random() * 100}%`,
            scale: Math.random() * 0.5 + 0.5,
            opacity: Math.random() * 0.2 + 0.25,
          }}
          animate={{
            y: ["0%", `${Math.random() > 0.5 ? '-' : ''}20%`],
            x: ["0%", `${Math.random() > 0.5 ? '-' : ''}16%`],
            transition: { repeat: Infinity, duration: 6 + Math.random() * 6, ease: "easeInOut", delay: i * 0.3 }
          }}
          className={`absolute w-8 h-8 rounded-full bg-gradient-to-tr ${i%2 ? "from-yellow-300 via-pink-400 to-blue-400" : "from-yellow-200 via-blue-400 to-red-600"} blur-2xl pointer-events-none`}
        />
      ))}
    </div>
  );
}

// ----- SVG Crack Divider -----
function CrackSVG() {
  return (
    <svg className="absolute left-1/2 -translate-x-1/2 top-20 bottom-20 z-30 h-[66vh] w-8 select-none pointer-events-none"
      viewBox="0 0 40 400" fill="none">
      <polyline
        points="20,0 24,36 12,78 26,123 16,186 31,224 15,292 22,335 18,370 20,400"
        stroke="#fff"
        strokeWidth="5.5"
        strokeLinejoin="round"
        strokeLinecap="round"
        style={{ filter: "drop-shadow(0 0 8px #fff8) drop-shadow(0 0 18px #fefefe88)" }}
      />
    </svg>
  );
}

// ----- VS Badge -----
function VSGoldBadge() {
  return (
    <motion.div
      initial={{ scale: 1.6, opacity: 0.0 }}
      animate={{ scale: [1.8, 1], opacity: [0.8, 1] }}
      transition={{ duration: 1.05, type: "spring" }}
      className="absolute top-6 left-1/2 -translate-x-1/2 z-40 pointer-events-none select-none"
    >
      <span
        className="uppercase tracking-wide font-extrabold text-center drop-shadow-[0_0_32px_var(--main-gold),0_0_64px_#ef4444]"
        style={{
          fontFamily: "'Bebas Neue', 'Impact', 'Arial Black', sans-serif",
          fontSize: "clamp(2.5rem,10vw,5.6rem)",
          background: "linear-gradient(92deg,#ffe599 5%,#ffd700 50%,#fbbf24 90%)",
          WebkitBackgroundClip: "text",
          WebkitTextFillColor: "transparent",
          textShadow: "0 0 44px #fffbb8, 0 0 14px #eab30899"
        }}
      >VS</span>
    </motion.div>
  );
}

// ----- HealthBar -----
function HealthBar({ color = "blue", percent = 100 }) {
  const gradient =
    color === "blue"
      ? "from-blue-400 via-blue-500 to-cyan-300"
      : "from-rose-400 via-red-500 to-yellow-300";
  return (
    <div className="w-[280px] h-5 rounded-full bg-zinc-800/90 shadow-inner border-2 border-zinc-700 mb-2 relative overflow-hidden">
      <motion.div
        initial={false}
        animate={{ width: `${percent}%` }}
        transition={{ type: "spring", stiffness: 260, damping: 22 }}
        className={`h-full rounded-full bg-gradient-to-r ${gradient} shadow-[0_0_16px_1px_rgba(255,255,255,0.2)]`}
        style={{ minWidth: "8px" }}
      />
      <div className="absolute inset-0 pointer-events-none rounded-full" style={{background:"linear-gradient(to bottom,#fff4 20%,#fff1 60%,transparent 100%)", opacity:0.25}} />
    </div>
  );
}

// ----- FaceOffPanel Main -----
export default function FaceOffPanel({
  videoA, videoB,
  revealedA, revealedB,
  onRevealA, onRevealB,
  onVote,
  voteRegistered,
  votedFor,
  onNextMatch
}) {
  const [collide, setCollide] = useState(false);
  const [flash, setFlash] = useState(false);
  const [showWinner, setShowWinner] = useState(false);

  useEffect(() => {
    if (revealedA && revealedB && !collide) {
      setTimeout(() => setFlash(true), 200);
      setTimeout(() => setCollide(true), 420);
      setTimeout(() => setFlash(false), 650);
    }
    if (!(revealedA && revealedB)) setCollide(false);
  }, [revealedA, revealedB]);

  useEffect(() => {
    setShowWinner(false);
  }, [videoA?.ytId, videoB?.ytId]);

  useEffect(() => {
    if (votedFor) {
      setTimeout(() => setShowWinner(true), 650);
    } else {
      setShowWinner(false);
    }
  }, [votedFor]);

  const [blueHP, redHP] = !votedFor ? [100, 100] : votedFor === "A" ? [100, 0] : [0, 100];

  // Explosion flash
  const explosion = flash ? (
    <motion.div
      className="absolute inset-0 z-50 pointer-events-none"
      initial={{ opacity: 0.86 }}
      animate={{ opacity: 0 }}
      transition={{ duration: 0.27 }}
      style={{
        background: "radial-gradient(circle, #fff 0%, #ffd700d2 40%, #be123c25 100%)",
        mixBlendMode: "screen"
      }}
    />
  ) : null;

  // Frame style for both
  const frameStyle = "w-[380px] h-[215px] max-w-[96vw] flex flex-col justify-end items-center bg-black/95 rounded-3xl shadow-2xl border-4 overflow-hidden relative transition-all duration-300";
  const titleOverlay = "absolute left-0 bottom-0 w-full h-[2.9em] flex items-center justify-center px-4 text-lg font-extrabold text-white text-center tracking-tight shadow-lg whitespace-nowrap overflow-hidden truncate pointer-events-auto" +
    " bg-gradient-to-t from-black/80 via-black/50 to-transparent z-20";

  return (
    <div className="relative min-h-screen flex flex-col items-center justify-center overflow-hidden bg-[#080d18]">
      {/* Background effects */}
      <div className="absolute inset-0 z-0 pointer-events-none">
        <div className="absolute inset-0 bg-gradient-to-br from-[#090a1c] via-[#171d2e] to-[#090a1c] opacity-100" />
        <div className="absolute left-[-18vw] top-[-8vw] w-[45vw] h-[45vw] rounded-full blur-3xl bg-gradient-to-tr from-yellow-400 via-yellow-100/30 to-blue-600/30 opacity-20" />
        <div className="absolute right-[-20vw] bottom-[-10vw] w-[45vw] h-[45vw] rounded-full blur-3xl bg-gradient-to-br from-rose-400 via-red-600/20 to-blue-400/20 opacity-20" />
        <NoiseTexture />
      </div>
      <ParticleBG />
      <VSGoldBadge />
      <CrackSVG />
      {explosion}

      {/* Main battle row */}
      <div className="relative z-20 flex flex-row w-full max-w-6xl items-end justify-center gap-10 pt-32 pb-16 md:flex-row flex-col md:gap-10 gap-6">
        {/* Blue Side */}
        <div className="flex-1 flex flex-col items-center justify-center">
          <HealthBar color="blue" percent={blueHP} />
          <div className="relative">
            {!revealedA ? (
              <motion.button
                initial={{ scale: 0.97, opacity: 0.5 }}
                animate={{ scale: 1, opacity: 1 }}
                whileHover={{ scale: 1.08, backgroundColor: "#2563eb" }}
                whileTap={{ scale: 0.95 }}
                onClick={onRevealA}
                className="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 z-20 bg-gradient-to-r from-blue-500 via-blue-400 to-cyan-400 px-12 py-6 rounded-full text-3xl font-black shadow-2xl border-4 border-blue-400 text-white focus:ring-2 ring-blue-300 transition-all"
                style={{ minWidth: 220, minHeight: 80 }}
              >
                Reveal
              </motion.button>
            ) : (
              <motion.div
                initial={{ x: -110, scale: 0.93, opacity: 0 }}
                animate={{
                  x: collide ? 0 : -50,
                  scale: votedFor === "A" ? 1.06 : votedFor === "B" ? 1 : 1,
                  opacity: 1,
                  filter: votedFor === "B" ? "grayscale(100%) brightness(0.72)" : "none"
                }}
                transition={{ type: "spring", stiffness: 280, damping: 18 }}
                className={`${frameStyle} border-blue-500 shadow-[0_0_48px_#3b82f680,0_0_4px_#3b82f6] ${collide && "ring-4 ring-blue-400/50"}`}
              >
                <iframe
                  width="100%" height="100%"
                  src={`https://www.youtube.com/embed/${videoA.ytId}`}
                  title={videoA.title}
                  frameBorder="0"
                  allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                  allowFullScreen
                  className="rounded-2xl"
                />
                {/* Title overlay, always at bottom, never shifts frame */}
                <div className={titleOverlay} title={videoA.title}>{videoA.title}</div>
              </motion.div>
            )}
          </div>
        </div>

        {/* Red Side */}
        <div className="flex-1 flex flex-col items-center justify-center">
          <HealthBar color="red" percent={redHP} />
          <div className="relative">
            {!revealedB ? (
              <motion.button
                initial={{ scale: 0.97, opacity: 0.5 }}
                animate={{ scale: 1, opacity: 1 }}
                whileHover={{ scale: 1.08, backgroundColor: "#be123c" }}
                whileTap={{ scale: 0.95 }}
                onClick={onRevealB}
                className="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 z-20 bg-gradient-to-r from-rose-500 via-red-400 to-yellow-400 px-12 py-6 rounded-full text-3xl font-black shadow-2xl border-4 border-red-400 text-white focus:ring-2 ring-red-300 transition-all"
                style={{ minWidth: 220, minHeight: 80 }}
              >
                Reveal
              </motion.button>
            ) : (
              <motion.div
                initial={{ x: 110, scale: 0.93, opacity: 0 }}
                animate={{
                  x: collide ? 0 : 50,
                  scale: votedFor === "B" ? 1.06 : votedFor === "A" ? 1 : 1,
                  opacity: 1,
                  filter: votedFor === "A" ? "grayscale(100%) brightness(0.72)" : "none"
                }}
                transition={{ type: "spring", stiffness: 280, damping: 18 }}
                className={`${frameStyle} border-red-500 shadow-[0_0_48px_#ef444480,0_0_4px_#ef4444] ${collide && "ring-4 ring-red-400/50"}`}
              >
                <iframe
                  width="100%" height="100%"
                  src={`https://www.youtube.com/embed/${videoB.ytId}`}
                  title={videoB.title}
                  frameBorder="0"
                  allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                  allowFullScreen
                  className="rounded-2xl"
                />
                {/* Title overlay, always at bottom, never shifts frame */}
                <div className={titleOverlay} title={videoB.title}>{videoB.title}</div>
              </motion.div>
            )}
          </div>
        </div>
      </div>

      {/* Voting Buttons */}
      <AnimatePresence>
        {collide && !voteRegistered && (
          <motion.div
            key="votes"
            initial={{ opacity: 0, y: 24 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: 24 }}
            transition={{ duration: 0.31, delay: 0.14 }}
            className="flex gap-10 z-30 mt-4 md:flex-row flex-col items-center"
          >
            <motion.button
              whileHover={{ scale: 1.07 }}
              whileTap={{ scale: 0.97 }}
              onClick={() => onVote("A")}
              className="text-2xl px-10 py-5 font-black rounded-full bg-gradient-to-r from-blue-500 via-blue-400 to-cyan-400 hover:from-blue-400 hover:to-cyan-200 text-white shadow-xl border-2 border-blue-500 uppercase tracking-wider"
            >
              Vote Blue Side
            </motion.button>
            <motion.button
              whileHover={{ scale: 1.07 }}
              whileTap={{ scale: 0.97 }}
              onClick={() => onVote("B")}
              className="text-2xl px-10 py-5 font-black rounded-full bg-gradient-to-r from-rose-500 via-red-400 to-yellow-400 hover:from-red-400 hover:to-yellow-200 text-white shadow-xl border-2 border-red-500 uppercase tracking-wider"
            >
              Vote Red Side
            </motion.button>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Next Match */}
      <AnimatePresence>
        {showWinner && voteRegistered && (
          <motion.div
            key="next"
            initial={{ opacity: 0, scale: 0.93 }}
            animate={{ opacity: 1, scale: 1 }}
            exit={{ opacity: 0 }}
            transition={{ duration: 0.29, delay: 0.24 }}
            className="flex z-40 mt-14"
          >
            <button
              onClick={onNextMatch}
              className="text-xl px-9 py-4 font-extrabold rounded-full bg-gradient-to-r from-yellow-400 via-yellow-300 to-yellow-500 text-[#684f1d] shadow-lg border-2 border-yellow-400 mx-auto uppercase"
            >
              Next Match
            </button>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}
