import React from 'react';

// Theme colors for Video A (blue), Video B (red), and neutral states
const themeColors = {
  blue: 'bg-[#2563eb]',  // Replace with blue
  red: 'bg-[#be123c]',   // Replace with red
  neutral: 'bg-[#240046]', // Neutral color for a draw or unranked
};

export default function BracketDisplay({ tournament }) {
  if (!tournament?.bracket || !Array.isArray(tournament.bracket)) return null;
  if (!Array.isArray(tournament.videos)) return <div className="text-red-500">No videos data</div>;
  if (tournament.bracket.length === 0) return <div className="text-gray-500">No rounds available.</div>;

  const getVoteLeader = (match) => {
    const votesA = (tournament.votes || []).filter(v => v.matchId === match.id && v.votedFor === match.videoAId).length;
    const votesB = (tournament.votes || []).filter(v => v.matchId === match.id && v.votedFor === match.videoBId).length;
    return votesA > votesB ? 'A' : (votesB > votesA ? 'B' : null);
  };

  return (
    <div className="overflow-x-auto w-full flex justify-center pb-10">
      <div className="flex gap-10 min-h-[350px]">
        {tournament.bracket.map((round, ridx) => (
          <div
            key={ridx}
            className="flex flex-col gap-5 min-w-[300px] bg-gradient-to-b from-[#240046] to-[#9d4edd] rounded-2xl shadow-lg border-2 border-[#9d4edd]/50 px-6 py-5" // Removed purple colors
          >
            <div className="font-extrabold text-2xl text-center tracking-wider text-[#f9eeff] drop-shadow-lg pb-2">
              Round {round.round}
            </div>
            {(round.matches || []).map((m, i) => {
              const vidA = tournament.videos.find(v => v.id === m.videoAId);
              const vidB = tournament.videos.find(v => v.id === m.videoBId);
              const leader = getVoteLeader(m);
              return (
                <div
                  key={m.id}
                  className={`bg-[#240046] border-2 border-[#9d4edd]/70 rounded-xl shadow-xl flex flex-col items-center py-6 px-4 hover:border-[#c77dff] transition-all cursor-pointer`} // Adjusted layout
                  onClick={() => alert(`Match ID: ${m.id}`)} // Show match analysis
                >
                  <div className="w-full flex flex-col items-center">
                    <span className={`font-bold text-lg text-center max-w-[200px] truncate ${leader === 'A' ? themeColors.blue : leader === 'B' ? themeColors.red : themeColors.neutral}`}>
                      {vidA?.title || 'A'}
                    </span>
                    <span className="text-[#f9eeff]/70 text-sm my-1">vs</span>
                    <span className={`font-bold text-lg text-center max-w-[200px] truncate ${leader === 'B' ? themeColors.red : leader === 'A' ? themeColors.blue : themeColors.neutral}`}>
                      {vidB?.title || 'B'}
                    </span>
                  </div>
                  <div className="text-xs text-[#f9eeff]/80 mt-3">
                    <span className="font-semibold text-[#c77dff]">A:</span> {(tournament.votes || []).filter(v => v.matchId === m.id && v.votedFor === m.videoAId).length}
                    <span className="mx-1 text-[#f9eeff]/50">|</span>
                    <span className="font-semibold text-[#be123c]">B:</span> {(tournament.votes || []).filter(v => v.matchId === m.id && v.votedFor === m.videoBId).length}
                  </div>
                </div>
              );
            })}
          </div>
        ))}
      </div>
    </div>
  );
}
