import React, { useEffect, useState } from "react";
import { motion, AnimatePresence } from "framer-motion";

// Neon Pulse Background
function NeonPulseBackground({ isPlaying }) {
  const [pulse, setPulse] = useState(0);

  useEffect(() => {
    if (!isPlaying) {
      setPulse(0);
      return;
    }
    let frame = 0;
    let running = true;
    const animate = () => {
      setPulse(Math.abs(Math.sin(frame / 20)));
      frame += 1;
      if (running) requestAnimationFrame(animate);
    };
    animate();
    return () => { running = false; };
  }, [isPlaying]);

  return (
    <div className="fixed inset-0 z-0 pointer-events-none">
      <div style={{
        position: "absolute",
        inset: 0,
        display: "flex",
      }}>
        <div
          style={{
            flex: 1,
            background: `radial-gradient(circle at 40% 50%, rgba(0,155,255,${0.13 + 0.29 * pulse}) 0%, transparent 70%)`,
            boxShadow: `0 0 ${40 + 70 * pulse}px 10px rgba(0,155,255,${0.28 + 0.6 * pulse})`,
            transition: "box-shadow 0.12s, background 0.12s",
            borderTopRightRadius: "60px",
            borderBottomRightRadius: "60px"
          }}
        />
        <div
          style={{
            flex: 1,
            background: `radial-gradient(circle at 60% 50%, rgba(255,20,40,${0.13 + 0.29 * pulse}) 0%, transparent 70%)`,
            boxShadow: `0 0 ${40 + 70 * pulse}px 10px rgba(255,30,70,${0.28 + 0.6 * pulse})`,
            transition: "box-shadow 0.12s, background 0.12s",
            borderTopLeftRadius: "60px",
            borderBottomLeftRadius: "60px"
          }}
        />
      </div>
    </div>
  );
}

function CrackSVG() {
  return (
    <svg className="crack-svg" viewBox="0 0 40 250" fill="none">
      <polyline
        points="20,0 22,36 14,62 23,95 13,120 27,152 18,180 21,220 20,250"
        stroke="#fff"
        strokeWidth="3.3"
        strokeLinejoin="round"
        strokeLinecap="round"
      />
    </svg>
  );
}

export default function FaceOffPanel({
  videoA, videoB,
  revealedA, revealedB,
  onRevealA, onRevealB,
  onVote,
  voteRegistered,
  votedFor,
  onNextMatch
}) {
  const [collide, setCollide] = useState(false);
  const [flash, setFlash] = useState(false);
  const [showWinner, setShowWinner] = useState(false);
  const [showTitleA, setShowTitleA] = useState(false);
  const [showTitleB, setShowTitleB] = useState(false);
  const [isPlaying, setIsPlaying] = useState(false);

  useEffect(() => {
    if (revealedA) setShowTitleA(true);
    if (revealedB) setShowTitleB(true);
  }, [revealedA, revealedB]);

  useEffect(() => {
    if (revealedA && revealedB && !collide) {
      setTimeout(() => setFlash(true), 240);
      setTimeout(() => setCollide(true), 440);
      setTimeout(() => setFlash(false), 650);
    }
    if (!(revealedA && revealedB)) setCollide(false);
  }, [revealedA, revealedB]);

  useEffect(() => {
    setShowWinner(false);
  }, [videoA?.ytId, videoB?.ytId]);

  useEffect(() => {
    if (votedFor) {
      setTimeout(() => setShowWinner(true), 700);
    } else {
      setShowWinner(false);
    }
  }, [votedFor]);

  const [blueHP, redHP] = !votedFor ? [100, 100] : votedFor === "A" ? [100, 0] : [0, 100];

  const VS = (
    <div className="fixed top-[2vw] left-1/2 -translate-x-1/2 z-40 pointer-events-none">
      <motion.div
        initial={{ scale: 1.4, opacity: 0.0 }}
        animate={{ scale: [1.7, 1], opacity: [0.72, 1] }}
        transition={{ duration: 1 }}
        className="text-[5vw] font-black text-center uppercase tracking-wide"
        style={{
          color: "var(--main-gold)",
          textShadow: "0 0 32px var(--main-gold), 0 0 64px #ef4444"
        }}
      >VS</motion.div>
    </div>
  );

  const explosion = flash ? (
    <motion.div
      className="absolute inset-0 z-40 pointer-events-none"
      initial={{ opacity: 0.86 }}
      animate={{ opacity: 0 }}
      transition={{ duration: 0.27 }}
      style={{
        background: "radial-gradient(circle, #fff 0%, #ffd70090 40%, #be123c15 100%)",
        mixBlendMode: "screen"
      }}
    />
  ) : null;

  // Title Reveal (animated, above video)
  const TitleReveal = ({ title, color }) => (
    <AnimatePresence>
      {title && (
        <motion.div
          initial={{ scale: 0.82, opacity: 0, y: -55 }}
          animate={{ scale: 1, opacity: 1, y: 0 }}
          exit={{ scale: 0.8, opacity: 0, y: -40 }}
          transition={{ duration: 0.55, type: "spring" }}
          className={`absolute w-full left-0 flex justify-center z-30`}
          style={{
            top: '-60px',
            pointerEvents: "none"
          }}
        >
          <span
            className={`text-[2vw] md:text-[2.5vw] font-bold uppercase tracking-wide px-5 py-2 rounded-full shadow-xl`}
            style={{
              background: color === "blue"
                ? "rgba(0,145,255,0.95)"
                : "rgba(255,28,56,0.97)",
              color: "#fff",
              border: color === "blue"
                ? "3px solid #0ff"
                : "3px solid #ff4b7d",
              textShadow: "0 0 15px #fff, 0 0 5px #0ff",
              letterSpacing: "0.12em",
              boxShadow:
                color === "blue"
                  ? "0 0 36px 3px #49e4ff99"
                  : "0 0 36px 3px #ff335599"
            }}
          >
            {title}
          </span>
        </motion.div>
      )}
    </AnimatePresence>
  );

  // --- Main Render ---
  return (
    <div className="battle-bg relative min-h-screen flex flex-col items-center justify-center">
      <NeonPulseBackground isPlaying={isPlaying} />
      <div className="faceoff-crack">
        <CrackSVG />
      </div>
      {explosion}
      {VS}

      <div className="relative z-10 flex flex-row w-full max-w-7xl items-center justify-center gap-20 pt-24 pb-16">
        {/* Blue Side */}
        <div className="flex-1 flex flex-col items-center justify-center">
          <div className="healthbar-outer mb-4" style={{ width: '300px' }}>
            <div
              className="healthbar-inner healthbar-blue"
              style={{ width: blueHP + "%" }}
            />
          </div>
          <div className="relative w-[460px] flex flex-col items-center">
            {/* Reveal button above */}
            {!revealedA && (
              <motion.button
                initial={{ scale: 0.97, opacity: 0.5 }}
                animate={{ scale: 1, opacity: 1 }}
                whileHover={{ scale: 1.06, backgroundColor: "#2563eb" }}
                whileTap={{ scale: 0.95 }}
                onClick={onRevealA}
                className="absolute -top-14 left-1/2 -translate-x-1/2 z-20 bg-blue_side px-10 py-4 rounded-xl text-xl font-bold shadow-2xl border-4 border-blue-500 text-white focus:ring-2 ring-blue-400 transition-all"
              >
                Reveal
              </motion.button>
            )}
            <TitleReveal title={showTitleA ? videoA.title : ""} color="blue" />
            {revealedA ? (
              <motion.div
                initial={{ x: -120, scale: 0.94, opacity: 0 }}
                animate={{
                  x: collide ? 0 : -60,
                  scale: votedFor === "A" ? 1.08 : votedFor === "B" ? 1 : 1,
                  opacity: 1,
                  filter: votedFor === "B" ? "grayscale(100%)" : "none"
                }}
                transition={{ type: "spring", stiffness: 250, damping: 16 }}
                className={`w-[460px] h-[260px] bg-black rounded-2xl shadow-2xl border-4 border-blue-500 relative overflow-hidden flex items-center ${collide && "battle-border-blue"}`}
              >
                <iframe
                  width="100%" height="100%"
                  src={`https://www.youtube.com/embed/${videoA.ytId}`}
                  title={videoA.title}
                  frameBorder="0"
                  allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                  allowFullScreen
                  className="rounded-2xl"
                  onLoad={() => setIsPlaying(true)}
                />
              </motion.div>
            ) : (
              <div className="w-[460px] h-[260px]" />
            )}
          </div>
        </div>

        {/* Red Side */}
        <div className="flex-1 flex flex-col items-center justify-center">
          <div className="healthbar-outer mb-4" style={{ width: '300px' }}>
            <div
              className="healthbar-inner healthbar-red"
              style={{ width: redHP + "%" }}
            />
          </div>
          <div className="relative w-[460px] flex flex-col items-center">
            {!revealedB && (
              <motion.button
                initial={{ scale: 0.97, opacity: 0.5 }}
                animate={{ scale: 1, opacity: 1 }}
                whileHover={{ scale: 1.06, backgroundColor: "#be123c" }}
                whileTap={{ scale: 0.95 }}
                onClick={onRevealB}
                className="absolute -top-14 left-1/2 -translate-x-1/2 z-20 bg-red_side px-10 py-4 rounded-xl text-xl font-bold shadow-2xl border-4 border-red-500 text-white focus:ring-2 ring-red-400 transition-all"
              >
                Reveal
              </motion.button>
            )}
            <TitleReveal title={showTitleB ? videoB.title : ""} color="red" />
            {revealedB ? (
              <motion.div
                initial={{ x: 120, scale: 0.94, opacity: 0 }}
                animate={{
                  x: collide ? 0 : 60,
                  scale: votedFor === "B" ? 1.08 : votedFor === "A" ? 1 : 1,
                  opacity: 1,
                  filter: votedFor === "A" ? "grayscale(100%)" : "none"
                }}
                transition={{ type: "spring", stiffness: 250, damping: 16 }}
                className={`w-[460px] h-[260px] bg-black rounded-2xl shadow-2xl border-4 border-red-500 relative overflow-hidden flex items-center ${collide && "battle-border-red"}`}
              >
                <iframe
                  width="100%" height="100%"
                  src={`https://www.youtube.com/embed/${videoB.ytId}`}
                  title={videoB.title}
                  frameBorder="0"
                  allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                  allowFullScreen
                  className="rounded-2xl"
                  onLoad={() => setIsPlaying(true)}
                />
              </motion.div>
            ) : (
              <div className="w-[460px] h-[260px]" />
            )}
          </div>
        </div>
      </div>

      {/* Voting Buttons */}
      <AnimatePresence>
        {collide && !voteRegistered && (
          <motion.div
            key="votes"
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: 30 }}
            transition={{ duration: 0.34, delay: 0.2 }}
            className="flex gap-16 z-20 mt-4"
          >
            <motion.button
              whileHover={{ scale: 1.07 }}
              whileTap={{ scale: 0.97 }}
              onClick={() => onVote("A")}
              className="text-2xl px-12 py-5 font-black rounded-2xl bg-blue_side hover:bg-blue-400 text-white shadow-xl border-2 border-blue-500 uppercase tracking-wider"
            >
              Vote Blue Side
            </motion.button>
            <motion.button
              whileHover={{ scale: 1.07 }}
              whileTap={{ scale: 0.97 }}
              onClick={() => onVote("B")}
              className="text-2xl px-12 py-5 font-black rounded-2xl bg-red_side hover:bg-red-400 text-white shadow-xl border-2 border-red-500 uppercase tracking-wider"
            >
              Vote Red Side
            </motion.button>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Next Match */}
      <AnimatePresence>
        {showWinner && voteRegistered && (
          <motion.div
            key="next"
            initial={{ opacity: 0, scale: 0.93 }}
            animate={{ opacity: 1, scale: 1 }}
            exit={{ opacity: 0 }}
            transition={{ duration: 0.32, delay: 0.27 }}
            className="flex z-30 mt-12"
          >
            <button
              onClick={onNextMatch}
              className="text-xl px-8 py-4 font-extrabold rounded-xl bg-[var(--main-gold)] text-[var(--main-dark)] shadow-lg border-2 border-[var(--main-gold-dark)] mx-auto uppercase"
            >
              Next Match
            </button>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}
