import React from "react";

// Custom theme colors
const themeColors = {
  blue: 'bg-blue-500',
  red: 'bg-red-500',
  winner: 'bg-green-500',
  neutral: 'bg-gray-800',
};

const BracketDisplay = ({ tournament }) => {
  // Helper function to get the winning side
  const getVoteLeader = (match) => {
    const votesA = (tournament.votes || []).filter(v => v.matchId === match.id && v.votedFor === match.videoAId).length;
    const votesB = (tournament.votes || []).filter(v => v.matchId === match.id && v.votedFor === match.videoBId).length;
    return votesA > votesB ? 'A' : (votesB > votesA ? 'B' : null);
  };

  if (!tournament || !tournament.bracket || !Array.isArray(tournament.bracket)) return <div>No tournament bracket available.</div>;
  if (!Array.isArray(tournament.videos)) return <div>No videos available.</div>;
  if (tournament.bracket.length === 0) return <div>No rounds available.</div>;

  return (
    <div className="overflow-x-auto w-full flex justify-center pb-10">
      <div className="flex gap-16 min-h-[500px]">
        {tournament.bracket.map((round, ridx) => (
          <div
            key={ridx}
            className="flex flex-col gap-10 min-w-[300px] bg-gradient-to-b from-[#240046] to-[#3c096c] rounded-2xl shadow-lg border-2 border-[#7b2cbf]/50 px-6 py-3"
          >
            <div className="font-extrabold text-xl text-center tracking-wider text-[#e0aaff] drop-shadow pb-2">
              Round {round.round}
            </div>

            {/* Render Matches */}
            {(round.matches || []).map((match) => {
              const vidA = tournament.videos.find(v => v.id === match.videoAId);
              const vidB = tournament.videos.find(v => v.id === match.videoBId);
              const leader = getVoteLeader(match);
              const votesA = (tournament.votes || []).filter(v => v.matchId === match.id && v.votedFor === match.videoAId).length;
              const votesB = (tournament.votes || []).filter(v => v.matchId === match.id && v.votedFor === match.videoBId).length;

              return (
                <div
                  key={match.id}
                  className={`flex flex-col items-center py-6 px-3 rounded-xl shadow-xl border-2 ${leader === 'A' ? themeColors.blue : leader === 'B' ? themeColors.red : themeColors.neutral}`}
                >
                  <div className="w-full flex flex-col items-center">
                    <span className={`font-bold text-lg text-center ${leader === 'A' ? 'text-blue-500' : 'text-gray-300'}`}>{vidA?.title || 'A'}</span>
                    <span className="text-[var(--main-gold)]/70 text-sm my-1">vs</span>
                    <span className={`font-bold text-lg text-center ${leader === 'B' ? 'text-red-500' : 'text-gray-300'}`}>{vidB?.title || 'B'}</span>
                  </div>

                  {/* Voting and Results */}
                  <div className="text-xs text-[#e0aaff]/80 mt-3 w-full flex justify-between">
                    <div className="w-full text-left">
                      <span className="font-semibold text-[#c77dff]">A:</span> {votesA}
                    </div>
                    <div className="w-full text-right">
                      <span className="font-semibold text-[#7b2cbf]">B:</span> {votesB}
                    </div>
                  </div>

                  {/* Vote Progress */}
                  <div className="w-full mt-2 h-2 bg-gray-400 rounded-full">
                    <div
                      className={`h-full ${leader === 'A' ? 'bg-blue-500' : 'bg-red-500'}`}
                      style={{ width: `${(votesA / (votesA + votesB)) * 100}%` }}
                    />
                  </div>
                </div>
              );
            })}
          </div>
        ))}
      </div>
    </div>
  );
};

export default BracketDisplay;
