import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { listenTournament, advanceRound } from '../firebase/firestore';
import BracketDisplay from './BracketDisplay';
import { PieChart, Pie, Cell, Tooltip, Legend } from 'recharts';

// Colors for the Pie chart
const COLORS = ['#ff6f00', '#ffbb33', '#ffd700', '#ffc107'];

function AdminPanel() {
  const { tid } = useParams();
  const [tournament, setTournament] = useState(null);
  const [canAdvance, setCanAdvance] = useState(false);
  const [loading, setLoading] = useState(false);
  const [votesData, setVotesData] = useState([]);

  useEffect(() => {
    const unsub = listenTournament(tid, (data) => {
      setTournament(data);
      setCanAdvance(true);
      // Gather vote statistics
      const stats = data.votes.reduce((acc, vote) => {
        const match = data.bracket.find(r => r.round === data.currentRound).matches.find(m => m.id === vote.matchId);
        if (!match) return acc;

        acc[match.id] = acc[match.id] || { A: 0, B: 0 };
        acc[match.id][vote.votedFor] += 1;
        return acc;
      }, {});
      setVotesData(stats);
    });
    return () => unsub && unsub();
  }, [tid]);

  async function handleAdvance() {
    setLoading(true);
    const currentRound = tournament.currentRound;
    const thisRound = tournament.bracket.find(r => r.round === currentRound);
    let winners = [];
    for (const match of thisRound.matches) {
      const votesA = (tournament.votes || []).filter(v => v.matchId === match.id && v.votedFor === match.videoAId).length;
      const votesB = (tournament.votes || []).filter(v => v.matchId === match.id && v.votedFor === match.videoBId).length;
      const winnerId = votesA >= votesB ? match.videoAId : match.videoBId;
      winners.push(winnerId);
    }

    let nextMatches = [];
    for (let i = 0; i < winners.length; i += 2) {
      if (winners[i + 1]) {
        nextMatches.push({
          id: crypto.randomUUID(),
          videoAId: winners[i],
          videoBId: winners[i + 1],
          votesA: 0,
          votesB: 0,
          votes: [],
          round: currentRound + 1
        });
      }
    }

    if (nextMatches.length === 0) {
      const winner = tournament.videos.find(v => v.id === winners[0]);
      alert("Tournament Complete! Winner: " + (winner?.title || "Unknown Video"));
      setLoading(false);
      return;
    }

    const newBracket = [
      ...tournament.bracket,
      { round: currentRound + 1, matches: nextMatches }
    ];
    await advanceRound(tid, newBracket, currentRound + 1);
    setLoading(false);
  }

  if (!tournament)
    return (
      <div className="flex justify-center items-center min-h-screen bg-[#10002b] text-[#c77dff] animate-fade-in">
        Loading...
      </div>
    );

  return (
    <div className="bg-gradient-to-br from-[#10002b] via-[#3c096c] to-[#240046] min-h-screen w-full flex flex-col items-center justify-start transition-all">
      {/* Tournament Overview */}
      <div className="mx-auto w-full max-w-3xl p-8 my-4 rounded-2xl bg-[#240046] border-2 border-[#7b2cbf] shadow-2xl flex flex-col gap-6 animate-fade-in">
        <h2 className="text-3xl font-black mb-2 text-[#c77dff] drop-shadow tracking-wider">Admin Panel: <span className="text-[#e0aaff]">{tournament.title}</span></h2>
        <div className="text-lg text-[#9d4edd] font-bold mb-2">
          Current Round: <span className="text-white">{tournament.currentRound}</span>
        </div>

        {/* Tournament Stats */}
        <div className="flex justify-between mb-4">
          <div className="text-lg font-semibold text-[#c77dff]">Total Votes</div>
          <div className="text-lg text-white">{tournament.votes.length}</div>
        </div>
        <div className="flex justify-between mb-4">
          <div className="text-lg font-semibold text-[#c77dff]">Active Players</div>
          <div className="text-lg text-white">{tournament.votes.filter(vote => vote.active).length}</div>
        </div>

        {/* Pie Chart for Voting Distribution */}
        <div className="mb-4">
          <h3 className="text-xl font-semibold text-[#c77dff] mb-2">Vote Distribution</h3>
          <PieChart width={200} height={200}>
            <Pie data={Object.values(votesData).map(v => ({ name: "A", value: v.A, color: "#7b2cbf" }))} dataKey="value" outerRadius={80} fill="#7b2cbf" label />
            <Pie data={Object.values(votesData).map(v => ({ name: "B", value: v.B, color: "#ff6f00" }))} dataKey="value" outerRadius={80} fill="#ff6f00" label />
            <Tooltip />
            <Legend />
          </PieChart>
        </div>
      </div>

      {/* Bracket Display */}
      <div className="mx-auto w-full max-w-3xl p-8 my-4 rounded-2xl bg-[#240046] border-2 border-[#7b2cbf] shadow-2xl flex flex-col gap-6 animate-fade-in">
        <h3 className="text-xl font-semibold text-[#c77dff] mb-4">Live Bracket</h3>
        <BracketDisplay tournament={tournament} />
      </div>

      {/* Advance Round Button */}
      <div className="mx-auto w-full max-w-3xl p-8 mt-8 rounded-2xl bg-[#240046] border-2 border-[#7b2cbf] shadow-2xl flex justify-center">
        <button
          className="py-4 rounded-xl bg-gradient-to-r from-[#7b2cbf] via-[#9d4edd] to-[#c77dff] text-[#10002b] font-black text-lg tracking-wide shadow-xl border-2 border-[#c77dff] hover:from-[#c77dff] hover:to-[#7b2cbf] transition-all duration-200 active:scale-95"
          disabled={!canAdvance || loading}
          onClick={handleAdvance}
        >
          {loading ? 'Advancing...' : 'Proceed to Next Round'}
        </button>
      </div>
    </div>
  );
}

export default AdminPanel;
