import React, { useState } from 'react';

// Palette for theme
const roundColors = [
  "from-[var(--main-dark)] to-[var(--main-gold-dark)]",   // Round 1
  "from-[#5a189a] to-[#9d4edd]",   // Round 2
  "from-[#240046] to-[#3c096c]",   // Round 3
  "from-[var(--main-gold-dark)] to-[#c77dff]",   // Round 4+
];

export default function BracketDisplay({ tournament }) {
  const [selectedMatch, setSelectedMatch] = useState(null); // For storing selected match for details

  if (!tournament?.bracket || !Array.isArray(tournament.bracket)) return null;
  if (!Array.isArray(tournament.videos)) return <div className="text-red-500">No videos data</div>;
  if (tournament.bracket.length === 0) return <div className="text-gray-500">No rounds available.</div>;

  const handleMatchClick = (match) => {
    setSelectedMatch(match); // Set selected match to show its details
  };

  const renderVoteProgress = (votesA, votesB) => {
    const totalVotes = votesA + votesB;
    const percentageA = totalVotes ? (votesA / totalVotes) * 100 : 0;
    const percentageB = totalVotes ? (votesB / totalVotes) * 100 : 0;

    return (
      <div className="w-full flex gap-2 mt-3">
        <div className="flex-1 bg-[#7b2cbf] rounded-lg h-2" style={{ width: `${percentageA}%` }} />
        <div className="flex-1 bg-[#ff6f00] rounded-lg h-2" style={{ width: `${percentageB}%` }} />
      </div>
    );
  };

  return (
    <div className="overflow-x-auto w-full flex justify-center pb-10">
      <div className="flex gap-10 min-h-[350px]">
        {tournament.bracket.map((round, ridx) => (
          <div
            key={ridx}
            className={`flex flex-col gap-5 min-w-[250px]
            bg-gradient-to-b ${roundColors[ridx % roundColors.length]} rounded-2xl shadow-lg border-2 border-[var(--main-gold-dark)]/50 px-4 py-3`}
          >
            <div className="font-extrabold text-xl text-center tracking-wider text-[var(--main-gold)] drop-shadow pb-2">
              Round {round.round}
            </div>
            {(round.matches || []).map((m, i) => {
              const vidA = tournament.videos.find(v => v.id === m.videoAId);
              const vidB = tournament.videos.find(v => v.id === m.videoBId);
              const votesA = (tournament.votes || []).filter(v => v.matchId === m.id && v.votedFor === m.videoAId).length;
              const votesB = (tournament.votes || []).filter(v => v.matchId === m.id && v.votedFor === m.videoBId).length;

              return (
                <div
                  key={m.id}
                  className="bg-[#240046] border-2 border-[var(--main-gold-dark)]/70 rounded-xl shadow-xl flex flex-col items-center py-4 px-2 hover:border-[#c77dff] transition-all cursor-pointer"
                  onClick={() => handleMatchClick(m)} // On click, show match details
                >
                  <div className="w-full flex flex-col items-center">
                    <span className="font-bold text-[#c77dff] text-lg text-center max-w-[200px] truncate">{vidA?.title || 'A'}</span>
                    <span className="text-[var(--main-gold)]/70 text-sm my-1">vs</span>
                    <span className="font-bold text-[#7b2cbf] text-lg text-center max-w-[200px] truncate">{vidB?.title || 'B'}</span>
                  </div>
                  {renderVoteProgress(votesA, votesB)}
                  <div className="text-xs text-[#e0aaff]/80 mt-3">
                    <span className="font-semibold text-[#c77dff]">A:</span> {votesA}
                    <span className="mx-1 text-[var(--main-gold)]/50">|</span>
                    <span className="font-semibold text-[#7b2cbf]">B:</span> {votesB}
                  </div>
                </div>
              );
            })}
          </div>
        ))}
      </div>

      {selectedMatch && (
        <div className="absolute inset-0 z-50 flex justify-center items-center bg-black/60">
          <div className="bg-[var(--main-dark)] p-6 rounded-2xl max-w-3xl text-white flex flex-col items-center gap-4">
            <h3 className="text-xl font-bold">{selectedMatch.videoAId} vs {selectedMatch.videoBId}</h3>
            <p className="text-md">Match Details: Voting Analysis...</p>
            {/* Here you could display more detailed stats like total votes, vote breakdown, etc. */}
            <button
              className="bg-[#ff6f00] px-6 py-2 rounded-xl"
              onClick={() => setSelectedMatch(null)} // Close the details view
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
